mapper.select:
SELECT line_id, line_name, line_link, line_img, priority, enable_status, create_time, last_edit_time FROM tb_head_line
and enable_status = #{headLineCondition.enableStatus}
ORDER BY priority DESC

mapper.update:
UPDATE tb_head_line
line_name=#{lineName}, line_link=#{lineLink}, line_ing=#{lineImg}, priority=#{priority}, enable_status=#{enableStatus}, last_edit_time=#{lastEditTime}
WHERE line_id = #{lineId}
