mapper.select:
SELECT
product_id, product_name, product_desc, img_addr, normal_price, promotion_price, priority, create_time, last_edit_time, enable_status,
point, product_category_id, shop_id
FROM
tb_product


        AND shop_id = #{productCondition.shop.shopId}


        AND product_category_id = #{productCondition.productCategory.productCategoryId}


        AND product_name LIKE '%${productCondition.productName}%'


        AND enable_status = #{productCondition.enableStatus}


ORDER BY
priority DESC
LIMIT #{rowIndex}, #{pageSize}

SELECT
count(1)
FROM
tb_product


        AND shop_id = #{productCondition.shop.shopId}


        ADN product_category_id = #{productCondition.productCategory.productCategoryId}


        AND product_name LIKE '%${productCondition.productName}%'


        AND enable_status = #{productCondition.enableStatus}

mapper.update:
UPDATE tb_shop

    shop_name = #{shopName},
    shop_desc = #{shopDesc},
    shop_addr = #{shopAddr},
    shop_img = #{shopImg},
    phone = #{phone},
     priority = #{priority}
    last_edit_time = #{lastEditTime}
    enable_status = #{enableStatus}
    advice = #{advice}
    area_id = #{area.area_id}
    shop_category_id = #{shopCategory.shopCategoryId}

WHERE shop_id = #{shopId}
